import React from 'react';
import {Link, RichText} from 'prismic-reactjs';
//import Sidebar from './Sidebar'
import CheeseburgerMenu from 'cheeseburger-menu'
import HamburgerMenu from 'react-hamburger-menu'

export default class Header extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      doc: null,
      notFound: false,
      menuOpen: false,

    }
    if (props.prismicCtx) {
      this.fetchPage(props);
    }
  }

  componentDidUpdate(prevProps) {
    this.props.prismicCtx.toolbar();
    // We fetch the page only after it's ready to query the api
    if (!prevProps.prismicCtx) {
      this.fetchPage(this.props);
    }
  }

  fetchPage(props) {
    if (props.prismicCtx) {
      // We use a single document query to get the only menu document
      return props.prismicCtx.api.getSingle('menu', {}, (err, doc) => {
        if (doc) {
          // We put the retrieved content in the state as a doc variable
          this.setState({ doc });
        } else {
          // We changed the state to display error not found if no matched doc
          this.setState({ notFound: !doc });
        }
      });
    }
    return null;
  }

  menuLinks() {
    return this.state.doc.data.menu_links.map((menuLink) => {
      //return < Sidebar/>;
      return (
        <li key={menuLink.link.id}>
          <a href={Link.url(menuLink.link, this.props.prismicCtx.linkResolver)}>
            {RichText.asText(menuLink.label)}
          </a>
        </li>
      );
    });
  }

  render() {
    if (this.state.doc) {
      return (
        <Navbar default collapseOnSelect>
          <Navbar.Header>
            <Navbar.Brand>
              <Link to="/">CodeLife</Link>
            </Navbar.Brand>
            <Navbar.Toggle />
          </Navbar.Header>

  <Nav.Link href="#features">Features</Nav.Link>
              <NavItem eventKey={1} componentClass={Link} href="/" to="/">
                Home
              </NavItem>
              <NavItem eventKey={2} componentClass={Link} href="/about" to="/about">
                About
              </NavItem>
              <NavItem eventKey={3} componentClass={Link} href="/news" to="/news">
                News
              </NavItem>


        </Navbar>



      );
    } else {
      return null
    }
  }
}
